import { Component, OnInit } from '@angular/core';
import { AuthService } from '../../../services/auth.service';
import { Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { AuthInterface } from 'src/app/interfaces/auth.interface';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styles:['ng-invalid.ng-touched{ border: solid 2px tomato }']
})
export class RegisterComponent implements OnInit {
  registerForm!: FormGroup
  resulForm!: AuthInterface
  constructor(
    private fb: FormBuilder,
    private authSvc: AuthService,
    private router: Router
  ) { }
  ngOnInit(): void {
    this.registerForm = this.intiForm()
  }

  onSubmit():void{
    this.authSvc.registrarUsuario(this.registerForm.value).subscribe(() => this.router.navigate(['/login']) )
  }

  intiForm(): FormGroup {
    return this.fb.group({
      nombre: ['',Validators.required],
      correo: ['',Validators.required],
      contrasenia: ['',Validators.required],
      direccion: ['',Validators.required],
      telefono: [0,Validators.required],
      fechaNacimiento: ['',Validators.required],
      idRoles: [1]
    })
  }
}
